// humanAPI auth button
function createHumanButton(){var e=document.createElement("input");e.setAttribute("type","button");e.setAttribute("value","connect human/api");e.setAttribute("name","humanAPIAuthButton");e.setAttribute("class","humanPopup");e.setAttribute("onclick","authHumanAPI()");humanButton[0].appendChild(e)}function popupwindow(e,t,n,r){var i=screen.width/2-n/2;var s=screen.height/2-r/2;return window.open(e,t,"toolbar=no, location=no, directories=no, status=no, menubar=no, scrollbars=no, resizable=no, copyhistory=no, width="+n+", height="+r+", top="+s+", left="+i)}function authHumanAPI(){var e=humanButton[0].getAttribute("data-callback");popupwindow(e,"humanapi-auth",600,humanButton[0].getAttribute('data-window-height'))}var humanButton=document.getElementsByClassName("humanAPIAuthButton");if(humanButton.length===0){console.log("please add humanAPIAuthButton span to page")}if(humanButton.length>=2){console.log("please only add one instance of humanAPIAuthButton to the page")}var css=document.createElement("style");css.type="text/css";css.innerHTML=".humanPopup { height: 50px!important;line-height: 36px;font-size: 20px;background: #ececec;font-weight:bold;padding: 0 1em;border: 1px solid #999;border-radius:5px;box-sizing:content-box;}.humanPopup:hover{border-color:#518cc6;border-bottom-color:#2a65a0;color:#fff;background-color:#599bdc;background-image:linear-gradient(#599bdc,#3072b3)}";document.body.appendChild(css);createHumanButton()